import React, { useState } from "react";
import axios from "axios";

const OfferServiceForm = ({ professionalId }) => {
  const [service, setService] = useState({
    nombre: "",
    descripcion: "",
    precio: "",
    categoria_id: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setService((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = JSON.stringify({
      ...service,
      profesional_id: professionalId,
    });

    try {
      const response = await fetch("http://localhost/Tarea3/backend/src/index.php/services", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: formData,
      });
      const data = await response.json();
      console.log(data);
      // handle success (e.g., show message, clear form)
    } catch (error) {
      console.error("There was an error submitting the form:", error);
      // handle error (e.g., show error message)
    }
  };


  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="nombre"
        value={service.nombre}
        onChange={handleChange}
        placeholder="Service Name"
        required
      />
      <textarea
        name="descripcion"
        value={service.descripcion}
        onChange={handleChange}
        placeholder="Service Description"
        required
      />
      <input
        type="number"
        name="precio"
        value={service.precio}
        onChange={handleChange}
        placeholder="Price"
        required
      />
      <select
        name="categoria_id"
        value={service.categoria_id}
        onChange={handleChange}
        required
      >
        {/* categories */}
        <option value="">Select Category</option>
        <option value="1">Cleaning</option>
        <option value="2">Plumbing</option>
        <option value="3">Car Washing</option>
      </select>
      <button type="submit">Submit</button>
    </form>
  );
};

export default OfferServiceForm;
