import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import ServiceItem from "./ServiceItem";

const servicesData = [
  {
    title: "Car Washing",
    image1: "assets/img/home-one/service1.png",
    image2: "assets/img/home-one/service2.png",
    link: "/services",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
  },

  {
    title: "Plumbing Service",
    image1: "assets/img/home-one/service3.png",
    image2: "assets/img/home-one/service4.png",
    link: "/services",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
  },

  {
    title: "House Cleaning",
    image1: "assets/img/home-one/service1.png",
    image2: "assets/img/home-one/service7.png",
    link: "/services",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
  },

  {
    title: "Office Cleaning",
    image1: "assets/img/home-one/service3.png",
    image2: "assets/img/home-one/service8.png",
    link: "/services",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
  },

  {
    title: "Carpet Cleaning",
    image1: "assets/img/home-one/service5.png",
    image2: "assets/img/home-one/service9.png",
    link: "/services",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
  },

  {
    title: "Window Cleaning",
    image1: "assets/img/home-one/service5.png",
    image2: "assets/img/home-one/service9.png",
    link: "/services",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
  },
];

const ServicesSection = () => {
  const [services, setServices] = useState([]);
  const { category } = useParams(); // assuming your route is /serviceSection/:category

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await fetch(
          `http://localhost/Tarea3/backend/src/index.php/services/${category}`
        );
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setServices(data); // update the state with the fetched data
      } catch (error) {
        console.error("Error fetching services:", error);
      }
    };

    fetchServices();
  }, [category]); // re-run the effect if the category changes

  return (
    <section className="service-area three pt-100 pb-70">
      <div className="container">
        <div className="section-title">
          <span className="sub-title">Our Services</span>
          <h2>We Are Committed To Give Our Best Services</h2>
        </div>
        <div className="row">
          {services.map((service, index) => (
            <ServiceItem
              key={index}
              title={service.title}
              image1={service.image1}
              image2={service.image2}
              link={`/services/${service.id}`} // Assuming there's a unique ID for each service
              description={service.description}
            />
          ))}
        </div>
      </div>
      {/* Pagination - placeholder for now */}
      <div className="pagination-area">
        <ul>
          <li>
            <Link to="#">Prev</Link>
          </li>
          <li>
            <Link to="#">1</Link>
          </li>
          <li>
            <Link to="#">2</Link>
          </li>
          <li>
            <Link to="#">3</Link>
          </li>
          <li>
            <Link to="#">Next</Link>
          </li>
        </ul>
      </div>
    </section>
  );
};

export default ServicesSection;
