import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import ServiceItem from "./ServiceItem";

const ServicesSection = () => {
  const [services, setServices] = useState([]);
  const { serviceName } = useParams();

  useEffect(() => {
    const fetchServicesByCategory = async () => {
      // Replace 'category_id' with the actual ID you get from the route or selection
      const category_id = serviceName; // You need to get this ID based on the selected service name
      try {
        const response = await fetch(
          `http://localhost/Tarea3/backend/src/index.php/services/${category_id}`
        );
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setServices(data);
      } catch (error) {
        console.error('Error fetching services by category:', error);
      }
    };

    if (serviceName) {
      fetchServicesByCategory();
    }
  }, [serviceName]);
  return (
    <section className="service-area three pt-100 pb-70">
      <div className="container">
        <div className="section-title">
          <span className="sub-title">Our Services</span>
          <h2>We Are Committed To Give Our Best Services</h2>
        </div>
        <div className="row">
          {services.map((service, index) => (
            <ServiceItem
              key={index}
              title={service.title}
              image1={service.image1}
              image2={service.image2}
              link={`/services/${service.id}`} // Assuming there's a unique ID for each service
              description={service.description}
            />
          ))}
        </div>
      </div>
      {/* Pagination - placeholder for now */}
      <div className="pagination-area">
        <ul>
          <li>
            <Link to="#">Prev</Link>
          </li>
          <li>
            <Link to="#">1</Link>
          </li>
          <li>
            <Link to="#">2</Link>
          </li>
          <li>
            <Link to="#">3</Link>
          </li>
          <li>
            <Link to="#">Next</Link>
          </li>
        </ul>
      </div>
    </section>
  );
};

export default ServicesSection;
