import React from 'react';

const SignIn = () => {
  // You would add form handling logic here
  const handleSubmit = (event) => {
    event.preventDefault();
    // Logic for handling the form submit
  };

  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <div className="user-item">
          <h2>Sign In</h2>
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-lg-12">
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Email or Phone"
                    // You might want to add state handling for these inputs
                  />
                </div>
              </div>
              <div className="col-lg-12">
                <div className="form-group">
                  <input
                    type="password"
                    className="form-control"
                    placeholder="Password"
                    // And here as well
                  />
                </div>
              </div>
              <div className="col-lg-12">
                <button type="submit" className="btn cmn-btn">Sign In</button>
                <span>Or</span>
                <a href="sign-up.html">Sign Up</a>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SignIn;
