import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./AuthContext";
const AuthForm = () => {
  const navigate = useNavigate();
  const { signIn } = useAuth();
  const [isSignIn, setIsSignIn] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombre, setNombre] = useState("");
  const [especialidad, setEspecialidad] = useState("");
  const [tipo, setTipo] = useState("user");
  const [errors, setErrors] = useState({});

  const validateInput = (name, value) => {
    if (value.trim() === "") {
      setErrors(prev => ({ ...prev, [name]: "This field is required" }));
      return false;
    } else {
      setErrors(prev => ({ ...prev, [name]: null }));
      return true;
    }
  };

  const handleChange = (name, value) => {
    if (name === "email") {
      setEmail(value);
      if (!/\S+@\S+\.\S+/.test(value)) {
        setErrors(prev => ({ ...prev, email: "Invalid email format" }));
      } else {
        validateInput(name, value);
      }
    } else if (name === "password") {
      setPassword(value);
      validateInput(name, value);
    } else if (name === "nombre") {
      setNombre(value);
      validateInput(name, value);
    } else if (name === "especialidad") {
      setEspecialidad(value);
      validateInput(name, value);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    let isValid = true;
    // Validate all fields before submitting
    [['email', email], ['password', password], ['nombre', nombre], ['especialidad', especialidad]].forEach(([key, value]) => {
      if (!validateInput(key, value)) {
        isValid = false;
      }
    });

    if (!isValid) {
      return; // Stop the form submission if validation fails
    }

    // If validation passes, proceed with the API call and rest of the logic
    const data = isSignIn ? await handleLogin() : await handleRegister();
    if (data && !data.error) {
      signIn(data);
      navigate("/");
    }
  };

  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <div className="user-item">
          <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
          <form onSubmit={handleSubmit}>
            {!isSignIn && (
              <>
                <div className="form-group">
                  <input
                    type="text"
                    className={`form-control ${errors.nombre ? 'is-invalid' : ''}`}
                    placeholder="Nombre"
                    value={nombre}
                    onChange={(e) => handleChange('nombre', e.target.value)}
                  />
                  {errors.nombre && <div className="error-message">{errors.nombre}</div>}
                </div>
                {tipo === "professional" && (
                  <div className="form-group">
                    <input
                      type="text"
                      className={`form-control ${errors.especialidad ? 'is-invalid' : ''}`}
                      placeholder="Especialidad"
                      value={especialidad}
                      onChange={(e) => handleChange('especialidad', e.target.value)}
                    />
                    {errors.especialidad && <div className="error-message">{errors.especialidad}</div>}
                  </div>
                )}
              </>
            )}
            <div className="form-group">
              <input
                type="email"
                className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                placeholder="Email"
                value={email}
                onChange={(e) => handleChange('email', e.target.value)}
              />
              {errors.email && <div className="error-message">{errors.email}</div>}
            </div>
            <div className="form-group">
              <input
                type="password"
                className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                placeholder="Password"
                value={password}
                onChange={(e) => handleChange('password', e.target.value)}
              />
              {errors.password && <div className="error-message">{errors.password}</div>}
            </div>
            {!isSignIn && (
              <div className="form-group">
                <label>
                  <input
                    type="checkbox"
                    checked={tipo === "professional"}
                    onChange={toggleTipo}
                  />
                  {" Register as a professional"}
                </label>
              </div>
            )}
            <div className="form-group">
              <button
                type="submit"
                className="btn cmn-btn"
                style={{ marginRight: "10px" }}
              >
                {isSignIn ? "Sign In" : "Sign Up"}
              </button>
              <button
                type="button"
                className="btn cmn-btn"
                onClick={toggleMode}
              >
                {isSignIn
                  ? "Need an account? Sign Up"
                  : "Have an account? Sign In"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
