import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Banner = () => {
  const [services, setServices] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch services on component mount
    const fetchServices = async () => {
      try {
        const response = await fetch(
          "http://localhost/Tarea3/backend/src/index.php/services"
        );
        if (!response.ok) {
          throw new Error(`Network response was not ok ${response.statusText}`);
        }
        const data = await response.json();
        setServices(data); // Set fetched services to state
      } catch (error) {
        console.error(
          "There has been a problem with your fetch operation:",
          error
        );
      }
    };

    fetchServices();
  }, []);

  // Handler when service is selected from the dropdown
  const handleServiceChange = (e) => {
    const serviceName = e.target.value;
    if (serviceName !== "Choose Your Service") {
      navigate(`/serviceSection/${serviceName}`); // Navigate to service detail page
    }
  };

  // Handler when the form is submitted
  const handleSubmit = (e) => {
    e.preventDefault();
    // Get the selected service from the form
    const serviceName = e.target.elements.service.value;
    if (serviceName && serviceName !== "Choose Your Service") {
      navigate(`/serviceSection/${serviceName}`); // Navigate to service detail page
    }
  };

  // Group services by category
  const groupedServices = services.reduce((acc, service) => {
    const { category = "Other" } = service; // Default category if none specified
    acc[category] = acc[category] || [];
    acc[category].push(service);
    return acc;
  }, {});
  return (
    <div className="banner-area three">
      <div className="banner-shape">
        {/* Banner images not shown for brevity */}
      </div>
      <div className="d-table">
        <div className="d-table-cell">
          <div className="container">
            <div className="banner-text">
              <h1>Best <span>Cleaning</span> Service Agency In The Town</h1>
              <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry.</p>
              <div className="banner-service">
                <form onSubmit={handleSubmit}>
                  <div className="row">
                    <div className="col-lg-8">
                      <div className="form-group">
                        <select 
                          className="nice-select"
                          onChange={handleServiceChange}
                          name="service"
                        >
                          <option value="">Choose Your Service</option>
                          {Object.entries(groupedServices).map(([category, services]) => (
                            <optgroup label={category} key={category}>
                              {services.map(service => (
                                <option key={service.servicio_id} value={service.nombre}>
                                  {service.nombre}
                                </option>
                              ))}
                            </optgroup>
                          ))}
                        </select>
                      </div>
                    </div>
                    <div className="col-lg-4">
                      <button type="submit" className="btn cmn-btn">
                        Book Now
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Banner;
