import React from "react";
import { Link } from "react-router-dom";

<button
  onClick={() => handleServiceRequest(service.servicio_id, professionalId)}
>
  Request Service
</button>;

// service request
const handleServiceRequest = async (servicioId, professionalId) => {
  const usuarioId = 3;
  try {
    const response = await fetch(
      `http://localhost/Tarea3/backend/src/index.php/reserva`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          usuario_id: usuarioId,
          profesional_id: professionalId,
          servicio_id: servicioId,
          estado: "pending",
        }),
      }
    );
    const data = await response.json();
    if (!response.ok) throw new Error(data.message || "An error occurred");
    alert("Service request sent successfully!");
  } catch (error) {
    console.error("There was an error sending the service request:", error);
  }
};

const ServiceItem = ({
  title,
  image1,
  image2,
  link,
  description,
  price,
  profesional,
}) => {
  return (
    <div className="col-sm-6 col-lg-4">
      <div className="service-item">
        <div className="service-top">
          <img src={image1} alt="Service" />
          <img src={image2} alt="Service" />
        </div>
        <h3>
          <Link to={link}>{title}</Link>
        </h3>
        <p>{description}</p>
        <p>Price: {price}</p>
        <p>Professional: {profesional || "Not Assigned"}</p>
        {/* <Link to={link} className="service-link">
          Learn More <i className="bx bx-right-arrow-alt"></i>
        </Link> */}
      </div>
    </div>
  );
};

export default ServiceItem;
