import React from "react";
import { Link } from "react-router-dom";
import OfferServiceForm from "./OfferServiceForm";
import { useAuth } from "./AuthContext";

// Mock data for project items
const projects = [
  {
    id: 1,
    title: "Interior Cleaning Service",
    imageUrl: "assets/img/home-one/project1.jpg",
    detailUrl: "/project-details/1",
  },
  {
    id: 2,
    title: "Home Cleaning",
    imageUrl: "assets/img/home-one/project2.jpg",
    detailUrl: "/project-details/2",
  },

  {
    id: 3,
    title: "Office Cleaning Service",
    imageUrl: "assets/img/home-one/project3.jpg",
    detailUrl: "/project-details/3",
  },

  {
    id: 4,
    title: "Home Cleaning Service",
    imageUrl: "assets/img/home-one/project5.jpg",
    detailUrl: "/project-details/5",
  },
];

// ProjectItem component
const ProjectItem = ({ id, title, imageUrl, detailUrl }) => (
  <div className="col-sm-6 col-lg-6">
    <div className="project-item">
      <div className="project-top">
        <Link to={detailUrl}>
          <img src={imageUrl} alt={title} />
        </Link>
      </div>
      <h3>
        <Link to={detailUrl}>{title}</Link>
      </h3>
      <Link to={detailUrl}>View Project</Link>
    </div>
  </div>
);

// ProjectsSection component
const ProjectsSection = () => {
  const { authState } = useAuth();
  const [professionalId, setProfessionalId] = useState(null);
  console.log(authState);

  useEffect(() => {
    const fetchProfessionalId = async () => {
      try {
        const response = await fetch(
          "http://localhost/Tarea3/backend/src/index.php/getProfessionalId", // Ensure this URL is correct
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ usuario_id: authState.usuario_id }),
          }
        );
        const data = await response.json();
        if (!data.error) {
          setProfessionalId(data.professional_id);
        }
      } catch (error) {
        console.error("Error fetching professional ID:", error);
      }
    };

    if (authState && authState.tipo === "professional") {
      fetchProfessionalId();
    }
  }, [authState]);
  return (
    <section className="project-area ptb-100">
      <div className="container">
        <div className="row">
          {projects.map((project) => (
            <ProjectItem key={project.id} {...project} />
          ))}
          {/* Servicesb form*/}
          {authState && authState.tipo === "professional" && (
            <OfferServiceForm professionalId={authState.profesional_id} />
          )}
        </div>

        {/* Pagination */}
        <div className="pagination-area">
          <ul>
            <li>
              <Link to="#">Prev</Link>
            </li>
            <li>
              <Link to="#">1</Link>
            </li>
            <li>
              <Link to="#">2</Link>
            </li>
            <li>
              <Link to="#">3</Link>
            </li>
            <li>
              <Link to="#">Next</Link>
            </li>
          </ul>
        </div>
      </div>
    </section>
  );
};

export default ProjectsSection;
