import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./AuthProvider";

const AuthForm = () => {
  const navigate = useNavigate();
  const { signIn } = useAuth();
  const [isSignIn, setIsSignIn] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombre, setNombre] = useState("");
  const [especialidad, setEspecialidad] = useState("");
  const [tipo, setTipo] = useState("user");

  const postCredentials = async (endpoint, credentials) => {
    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials),
      });

      if (!response.ok)
        throw new Error(`HTTP error! status: ${response.status}`);
      return response.json();
    } catch (error) {
      console.error("There was an error!", error);
    }
  };

  const handleLogin = async () => {
    const endpoint = "http://localhost/Tarea3/backend/src/index.php/login";
    const data = await postCredentials(endpoint, { email, password });

    if (data && !data.error) {
      console.log(data);
      navigate("/"); // Redirect to home upon successful login
    } else {
      console.error("Login failed:", data.message);
    }
  };

  const handleRegister = async () => {
    const endpoint =
      tipo === "professional"
        ? "http://localhost/Tarea3/backend/src/index.php/registerProfessional"
        : "http://localhost/Tarea3/backend/src/index.php/register";

    const payload = {
      nombre,
      email,
      password,
      tipo,
      ...(tipo === "professional" && { especialidad }),
    };

    const data = await postCredentials(endpoint, payload);

    if (data && !data.error) {
      console.log(data);
      navigate("/"); // Redirect to login page upon successful registration
    } else {
      console.error("Registration failed:", data.message);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    isSignIn ? handleLogin() : handleRegister();
