import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./AuthContext";

const AuthForm = () => {
  const navigate = useNavigate();
  const { signIn } = useAuth();
  const [isSignIn, setIsSignIn] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombre, setNombre] = useState("");
  const [especialidad, setEspecialidad] = useState("");
  const [tipo, setTipo] = useState("user");

  const postCredentials = async (endpoint, credentials) => {
    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials),
      });

      if (!response.ok)
        throw new Error(`HTTP error! status: ${response.status}`);
      return response.json();
    } catch (error) {
      console.error("There was an error!", error);
    }
  };

  const handleLogin = async () => {
    const endpoint = "http://localhost/Tarea3/backend/src/index.php/login";
    const data = await postCredentials(endpoint, { email, password });

    if (data && !data.error) {
      const userData = { ...data, userName: data.nombre }; // Adjust according to your API response
      signIn(userData);
      console.log(data);
      navigate("/"); // Redirect to home upon successful login
    } else {
      console.error("Login failed:", data.message);
    }
  };

  const handleRegister = async () => {
    const endpoint =
      tipo === "professional"
        ? "http://localhost/Tarea3/backend/src/index.php/registerProfessional"
        : "http://localhost/Tarea3/backend/src/index.php/register";

    const payload = {
      nombre,
      email,
      password,
      tipo,
      ...(tipo === "professional" && { especialidad }),
    };

    const data = await postCredentials(endpoint, payload);

    if (data && !data.error) {
      console.log(data);
      navigate("/"); // Redirect to login page upon successful registration
    } else {
      console.error("Registration failed:", data.message);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const data = isSignIn ? await handleLogin() : await handleRegister();
    if (data && !data.error) {
      signIn(data); // Update to use the actual data returned from your backend
      navigate("/"); // Redirect to home
    }
  };
  const toggleMode = () => {
    setIsSignIn(!isSignIn);
    setNombre(""); // Reset nombre when toggling between sign in and sign up
    setEspecialidad(""); // Reset especialidad when toggling between sign in and sign up
    setTipo("user"); // Reset tipo to 'user' when toggling modes
  };

  const toggleTipo = () => {
    setTipo(tipo === "user" ? "professional" : "user");
    if (tipo === "user") {
      setEspecialidad(""); // Clear especialidad when toggling away from 'professional'
    }
  };

  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <div className="user-item">
          <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
          <form onSubmit={handleSubmit}>
            {!isSignIn && (
              <>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Nombre"
                    value={nombre}
                    onChange={(e) => setNombre(e.target.value)}
                  />
                </div>
                {tipo === "professional" && (
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Especialidad"
                      value={especialidad}
                      onChange={(e) => setEspecialidad(e.target.value)}
                    />
                  </div>
                )}
              </>
            )}
            <div className="form-group">
              <input
                type="email"
                className="form-control"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                className="form-control"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            {!isSignIn && (
              <div className="form-group">
                <label>
                  <input
                    type="checkbox"
                    checked={tipo === "professional"}
                    onChange={toggleTipo}
                  />
                  {" Register as a professional"}
                </label>
              </div>
            )}
            <div className="form-group">
              <button
                type="submit"
                className="btn cmn-btn"
                style={{ marginRight: "10px" }}
              >
                {isSignIn ? "Sign In" : "Sign Up"}
              </button>
              <button
                type="button"
                className="btn cmn-btn"
                onClick={toggleMode}
              >
                {isSignIn
                  ? "Need an account? Sign Up"
                  : "Have an account? Sign In"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
