import React, { useState } from "react";

const AuthForm = () => {
  const [isSignIn, setIsSignIn] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombre, setNombre] = useState("");
  const [tipo, setTipo] = useState("user"); // Default to 'user'

  const handleLogin = async () => {
    const endpoint =
      tipo === "professional"
        ? "http://localhost/Tarea3/backend/src/index.php/loginProfessional"
        : "http://localhost/Tarea3/backend/src/index.php/login";

    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok)
        throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      console.log(data);
      // Handle the data from the backend
    } catch (error) {
      console.error("There was an error!", error);
    }
  };

  const handleRegister = async () => {
    const endpoint =
      tipo === "professional"
        ? "http://localhost/Tarea3/backend/src/index.php/registerProfessional"
        : "http://localhost/Tarea3/backend/src/index.php/register";

    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ nombre, email, password, tipo }),
      });

      if (!response.ok)
        throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      console.log(data);
      // Handle the data from the backend
    } catch (error) {
      console.error("There was an error!", error);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (isSignIn) {
      handleLogin();
    } else {
      handleRegister();
    }
  };

  const toggleMode = () => {
    setIsSignIn(!isSignIn);
  };

  const toggleTipo = () => {
    setTipo(tipo === "user" ? "professional" : "user");
  };
  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <div className="user-item">
          <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
          <form onSubmit={handleSubmit}>
            {/* Only show name field when registering */}
            {!isSignIn && (
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Nombre"
                  value={nombre}
                  onChange={(e) => setNombre(e.target.value)}
                />
              </div>
            )}

            {/* Email input */}
            <div className="form-group">
              <input
                type="email"
                className="form-control"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            {/* Password input */}
            <div className="form-group">
              <input
                type="password"
                className="form-control"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            {/* Toggle button to switch between user types */}
            <div className="form-group">
              <label>
                <input
                  type="checkbox"
                  checked={tipo === "professional"}
                  onChange={toggleTipo}
                />
                {" Register as a professional"}
              </label>
            </div>

            {/* Submit button */}
            <div className="form-group">
              <button type="submit" className="btn cmn-btn">
                {isSignIn ? "Sign In" : "Sign Up"}
              </button>
              <button type="button" className="btn-toggle" onClick={toggleMode}>
                {isSignIn ? "Need an account? Sign Up" : "Have an account? Sign In"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
