import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import ServiceItem from "./ServiceItem";

const ServicesSection = () => {
  const [services, setServices] = useState([]);
  const { serviceName } = useParams(); // This should match the dynamic segment in your route

  const servicesData = [
    {
      title: "Car Washing",
      image1: "assets/img/home-one/service1.png",
      image2: "assets/img/home-one/service2.png",
      link: "/services",
      description:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
    },
  
    {
      title: "Plumbing Service",
      image1: "assets/img/home-one/service3.png",
      image2: "assets/img/home-one/service4.png",
      link: "/services",
      description:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
    },
  
    {
      title: "House Cleaning",
      image1: "assets/img/home-one/service1.png",
      image2: "assets/img/home-one/service7.png",
      link: "/services",
      description:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
    },
  
    {
      title: "Office Cleaning",
      image1: "assets/img/home-one/service3.png",
      image2: "assets/img/home-one/service8.png",
      link: "/services",
      description:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
    },
  
    {
      title: "Carpet Cleaning",
      image1: "assets/img/home-one/service5.png",
      image2: "assets/img/home-one/service9.png",
      link: "/services",
      description:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
    },
  
    {
      title: "Window Cleaning",
      image1: "assets/img/home-one/service5.png",
      image2: "assets/img/home-one/service9.png",
      link: "/services",
      description:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. It has survived not only five centuries",
    },
  ];

  useEffect(() => {
    const fetchServices = async () => {
      try {
        console.log(`Fetching services for category: ${serviceName}`);
        const response = await fetch(
          `http://localhost/Tarea3/backend/src/index.php/services/${serviceName}`
        );
        const text = await response.text();
        console.log(`Response received: ${text}`);
        try {
          const data = JSON.parse(text);
          console.log(`Data parsed: `, data);
          setServices(data);
        } catch (error) {
          console.error("Failed to parse JSON:", text);
          console.error(error);
        }
      } catch (error) {
        console.error("There was an error fetching the service:", error);
      }
    };

    if (serviceName) {
      fetchServices();
    }
  }, [serviceName]);
  return (
    <section className="service-area three pt-100 pb-70">
      <div className="container">
        <div className="section-title">
          <span className="sub-title">Our Services</span>
          <h2>We Are Committed To Give Our Best Services</h2>
        </div>
        <div className="row">
          {services.length > 0 ? (
            services.map((service, index) => (
              <ServiceItem
                key={index}
                title={service.nombre} // Assuming your service object has these fields
                description={service.descripcion}
                price={service.precio}
              />
            ))
          ) : (
            <p>Loading services...</p>
          )}
        </div>
      </div>
      {/* Pagination - placeholder for now */}
      <div className="pagination-area">
        <ul>
          <li>
            <Link to="#">Prev</Link>
          </li>
          <li>
            <Link to="#">1</Link>
          </li>
          <li>
            <Link to="#">2</Link>
          </li>
          <li>
            <Link to="#">3</Link>
          </li>
          <li>
            <Link to="#">Next</Link>
          </li>
        </ul>
      </div>
    </section>
  );
};

export default ServicesSection;
