import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import OfferServiceForm from "./OfferServiceForm";
import { useAuth } from "./AuthContext";



// ProjectsSection component
const ProjectsSection = () => {
  const { authState } = useAuth();
  const [professionalId, setProfessionalId] = useState(null);

  console.log(authState);

  useEffect(() => {
    console.log("Sending usuario_id:", authState.usuario_id);
    const fetchProfessionalId = async () => {
      if (
        authState &&
        authState.tipo === "professional" &&
        authState.usuario_id
      ) {
        try {
          const response = await fetch(
            "http://localhost/Tarea3/backend/src/index.php/getProfessionalId",
            {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify({ usuario_id: authState.usuario_id }),
            }
          );
          const data = await response.json();
          if (!data.error && data.profesional_id) {
            setProfessionalId(data.profesional_id);
          } else {
            console.error("Error fetching professional ID:", data.message);
          }
        } catch (error) {
          console.error("Error fetching professional ID:", error);
        }
      }
    };

    fetchProfessionalId();
  }, [authState]);

  return (
    <section className="project-area ptb-100">
      <div className="container">
        <div className="row">
          {projects.map((project) => (
            <ProjectItem key={project.id} {...project} />
          ))}
          {/* Servicesb form*/}

          <OfferServiceForm profesional_id={professionalId} />
        </div>

        {/* Pagination */}
        <div className="pagination-area">
          <ul>
            <li>
              <Link to="#">Prev</Link>
            </li>
            <li>
              <Link to="#">1</Link>
            </li>
            <li>
              <Link to="#">2</Link>
            </li>
            <li>
              <Link to="#">3</Link>
            </li>
            <li>
              <Link to="#">Next</Link>
            </li>
          </ul>
        </div>
      </div>
    </section>
  );
};

export default ProjectsSection;
