import React, { useState } from "react";

const AuthForm = () => {
  // Single form state object to handle all form inputs
  const [form, setForm] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    fullName: "",
  });

  // Toggle between sign in and sign up
  const [isSignIn, setIsSignIn] = useState(true);

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({
      ...prevForm,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    if (isSignIn) {
      // Sign-in logic
      console.log("Sign In Data:", {
        emailOrPhone: form.emailOrPhone,
        password: form.password,
      });
      // Send sign-in data to backend
    } else {
      // Sign-up logic
      console.log("Sign Up Data:", form);
      // Send sign-up data to backend
    }
    // Reset form after submission
    setForm({
      email: "",
      password: "",
      confirmPassword: "",
      fullName: "",
    });
  };

  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <div className="user-item">
          <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
          <form onSubmit={handleSubmit}>
            <div className="row">
              {!isSignIn && (
                <>
                  <div className="col-lg-6 form-group">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Full Name"
                      name="firstName"
                      value={form.firstName}
                      onChange={handleChange}
                    />
                  </div>
                  <div className="col-lg-6 form-group">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Last Name"
                      name="lastName"
                      value={form.lastName}
                      onChange={handleChange}
                    />
                  </div>
                </>
              )}
              <div className="col-lg-12 form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder={isSignIn ? "Email or Phone" : "Email"}
                  name="emailOrPhone"
                  value={form.emailOrPhone}
                  onChange={handleChange}
                />
              </div>
              {!isSignIn && (
                <div className="col-lg-6 form-group">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Phone"
                    name="phone"
                    value={form.phone}
                    onChange={handleChange}
                  />
                </div>
              )}
              <div className="col-lg-12 form-group">
                <input
                  type="password"
                  className="form-control"
                  placeholder="Password"
                  name="password"
                  value={form.password}
                  onChange={handleChange}
                />
              </div>
              {!isSignIn && (
                <div className="col-lg-12 form-group">
                  <input
                    type="password"
                    className="form-control"
                    placeholder="Confirm Password"
                    name="confirmPassword"
                    value={form.confirmPassword}
                    onChange={handleChange}
                  />
                </div>
              )}
              <div className="col-lg-12 form-group">
                <button type="submit" className="btn cmn-btn">
                  {isSignIn ? "Sign In" : "Sign Up"}
                </button>
                <button
                  type="button"
                  className="btn cmn-btn"
                  onClick={() => setIsSignIn(!isSignIn)}
                >
                  {isSignIn
                    ? "Need an account? Sign Up"
                    : "Have an account? Sign In"}
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
