import React, { useState } from "react";

const OfferServiceForm = ({ professionalId }) => {
  const [service, setService] = useState({
    nombre: "",
    descripcion: "",
    precio: "",
    categoria_id: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setService((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = JSON.stringify({
      ...service,
      profesional_id: professionalId,
    });

    try {
      const response = await fetch(
        "http://localhost/Tarea3/backend/src/index.php/services",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: formData,
        }
      );
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error("There was an error submitting the form:", error);
    }
  };

  return (
    <div className="estimate-area ptb-100">
      <div className="container">
        <div className="estimate-content">
          <div className="section-title">
            <h2>Offer Your Services</h2>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-lg-6">
                <div className="form-group">
                  <input
                    type="text"
                    name="nombre"
                    className="form-control"
                    value={service.nombre}
                    onChange={handleChange}
                    placeholder="Service Name"
                    required
                  />
                </div>
              </div>
              <div className="col-lg-6">
                <div className="form-group">
                  <input
                    type="text"
                    name="precio"
                    className="form-control"
                    value={service.precio}
                    onChange={handleChange}
                    placeholder="Price"
                    required
                  />
                </div>
              </div>
              <div className="col-lg-12">
                <div className="form-group">
                  <textarea
                    name="descripcion"
                    rows="6"
                    className="form-control"
                    value={service.descripcion}
                    onChange={handleChange}
                    placeholder="Service Description"
                    required
                  ></textarea>
                </div>
              </div>
              <div className="col-lg-12">
                <div className="form-group">
                  <select
                    name="categoria_id"
                    value={service.categoria_id}
                    className="form-control"
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Category</option>
                    <option value="1">Cleaning</option>
                    <option value="2">Plumbing</option>
                    <option value="3">Car Washing</option>
                  </select>
                </div>
              </div>
              <div className="col-lg-12">
                <button type="submit" className="btn cmn-btn">
                  Offer Service
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default OfferServiceForm;
