import React, { useState } from "react";

const AuthForm = () => {
  const [isSignIn, setIsSignIn] = useState(true); // Toggle between sign in and sign up
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [type, setType] = useState(userType); // This could be 'client' or 'professional', you might handle this differently

  const handleSubmit = (event) => {
    event.preventDefault();
    const userData = {
      nombre: name,
      Email: email,
      contraseña: password,
      tipo: type, // This needs to be handled according to how you set up your database schema
    };
    // Here you will need to integrate with your backend API to handle sign up/sign in
    console.log(userData);
  };

  // Function to toggle between sign in and sign up mode
  const toggleForm = () => {
    setIsSignIn(!isSignIn);
    // Reset form fields when switching modes
    setName("");
    setEmail("");
    setPassword("");
  };

 return (
    <div className="auth-form">
      <h2>{isSignIn ? 'Sign In' : 'Sign Up'} as {userType}</h2>
      <form onSubmit={handleSubmit}>
        {!isSignIn && (
          <input
            type="text"
            className="form-control"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        )}
        <input
          type="email"
          className="form-control"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          className="form-control"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <div className="form-group">
          <button type="submit" className="btn cmn-btn">
            {isSignIn ? 'Sign In' : 'Sign Up'}
          </button>
          <button type="button" className="btn-toggle" onClick={toggleForm}>
            {isSignIn ? 'Need an account? Sign Up' : 'Have an account? Sign In'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AuthForm;
