import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./AuthContext";
import "./"

const AuthForm = () => {
  const navigate = useNavigate();
  const { signIn } = useAuth();
  const [isSignIn, setIsSignIn] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombre, setNombre] = useState("");
  const [especialidad, setEspecialidad] = useState("");
  const [tipo, setTipo] = useState("user");
  const [errors, setErrors] = useState({});

  const validateInput = (name, value) => {
    if (value.trim() === "") {
      setErrors((prev) => ({ ...prev, [name]: "This field is required" }));
      return false;
    } else {
      setErrors((prev) => ({ ...prev, [name]: null }));
      return true;
    }
  };

  const handleChange = (name, value) => {
    // Update state and validate input
    if (name === "email") {
      setEmail(value);
      validateInput(name, value);
    } else if (name === "password") {
      setPassword(value);
      validateInput(name, value);
    } else if (name === "nombre") {
      setNombre(value);
      validateInput(name, value);
    } else if (name === "especialidad") {
      setEspecialidad(value);
      validateInput(name, value);
    }
  };

  const toggleTipo = () => {
    setTipo(tipo === "user" ? "professional" : "user");
    if (tipo === "user") {
      setEspecialidad("");
    }
  };

  const toggleMode = () => {
    setIsSignIn(!isSignIn);
    setNombre("");
    setEspecialidad("");
    setTipo("user");
    setEmail("");
    setPassword("");
    setErrors({});
  };

  const postCredentials = async (endpoint, credentials) => {
    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials),
      });

      if (!response.ok)
        throw new Error(`HTTP error! status: ${response.status}`);
      return response.json();
    } catch (error) {
      console.error("There was an error!", error);
    }
  };

  const handleLogin = async () => {
    const endpoint = "http://localhost/Tarea3/backend/src/index.php/login";
    const data = await postCredentials(endpoint, { email, password });

    if (data && !data.error) {
      signIn({ ...data, userName: data.nombre });
      navigate("/");
    } else {
      console.error("Login failed:", data.message);
    }
  };

  const handleRegister = async () => {
    const endpoint =
      tipo === "professional"
        ? "http://localhost/Tarea3/backend/src/index.php/registerProfessional"
        : "http://localhost/Tarea3/backend/src/index.php/register";

    const payload = {
      nombre,
      email,
      password,
      tipo,
      ...(tipo === "professional" && { especialidad }),
    };

    const data = await postCredentials(endpoint, payload);

    if (data && !data.error) {
      navigate("/");
    } else {
      console.error("Registration failed:", data.message);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (isSignIn) {
      await handleLogin();
    } else {
      await handleRegister();
    }
  };

  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <div className="user-item">
          <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
          <form onSubmit={handleSubmit}>
            {!isSignIn && (
              <>
                <div className="form-group">
                  <input
                    type="text"
                    className={`form-control ${
                      errors.nombre ? "is-invalid" : ""
                    }`}
                    placeholder="Nombre"
                    value={nombre}
                    onChange={(e) => handleChange("nombre", e.target.value)}
                  />
                  {errors.nombre && (
                    <div className="error-message">{errors.nombre}</div>
                  )}
                </div>
                {tipo === "professional" && (
                  <div className="form-group">
                    <input
                      type="text"
                      className={`form-control ${
                        errors.especialidad ? "is-invalid" : ""
                      }`}
                      placeholder="Especialidad"
                      value={especialidad}
                      onChange={(e) =>
                        handleChange("especialidad", e.target.value)
                      }
                    />
                    {errors.especialidad && (
                      <div className="error-message">{errors.especialidad}</div>
                    )}
                  </div>
                )}
              </>
            )}
            <div className="form-group">
              <input
                type="email"
                className={`form-control ${errors.email ? "is-invalid" : ""}`}
                placeholder="Email"
                value={email}
                onChange={(e) => handleChange("email", e.target.value)}
              />
              {errors.email && (
                <div className="error-message">{errors.email}</div>
              )}
            </div>
            <div className="form-group">
              <input
                type="password"
                className={`form-control ${
                  errors.password ? "is-invalid" : ""
                }`}
                placeholder="Password"
                value={password}
                onChange={(e) => handleChange("password", e.target.value)}
              />
              {errors.password && (
                <div className="error-message">{errors.password}</div>
              )}
            </div>
            {!isSignIn && (
              <div className="form-group">
                <label>
                  <input
                    type="checkbox"
                    checked={tipo === "professional"}
                    onChange={toggleTipo}
                  />
                  {" Register as a professional"}
                </label>
              </div>
            )}
            <div className="form-group">
              <button
                type="submit"
                className="btn cmn-btn"
                style={{ marginRight: "10px" }}
              >
                {isSignIn ? "Sign In" : "Sign Up"}
              </button>
              <button
                type="button"
                className="btn cmn-btn"
                onClick={toggleMode}
              >
                {isSignIn
                  ? "Need an account? Sign Up"
                  : "Have an account? Sign In"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};
export default AuthForm;
