import { Link } from "react-router-dom";
import React, { useState } from "react";
import { useAuth } from "../components/AuthContext";

const Navbar = () => {
  const { authState, signOut: signOutContext } = useAuth();
  const [isSignUpDropdownOpen, setIsSignUpDropdownOpen] = useState(false);
  const navigate = useNavigate();

  const signOut = () => {
    signOutContext();
    navigate("/");
  };

  const toggleSignUpDropdown = (event) => {
    event.preventDefault();
    setIsSignUpDropdownOpen(!isSignUpDropdownOpen);
  };
  return (
    <div className="navbar-area two fixed-top">
      {/* Menu For Mobile Device */}
      <div className="mobile-nav">
        <Link to="/" className="logo">
          <img
            src={`${process.env.PUBLIC_URL}/assets/img/logo.png`}
            alt="Logo"
          />
        </Link>
      </div>

      {/* Menu For Desktop Device */}
      <div className="main-nav">
        <div className="container">
          <nav className="navbar navbar-expand-md navbar-light">
            <Link to="/" className="navbar-brand">
              <img src="/assets/img/logo.png" alt="Logo" />
            </Link>
            <div
              className="collapse navbar-collapse mean-menu"
              id="navbarSupportedContent"
            >
              <ul className="navbar-nav">
                <li className="nav-item">
                  <Link to="/" className="nav-link active">
                    Home
                  </Link>
                </li>
                <li className="nav-item">
                  <Link to="/about" className="nav-link">
                    About
                  </Link>
                </li>
                <li className="nav-item">
                  <Link to="/services" className="nav-link">
                    Services
                  </Link>
                </li>
                <li className="nav-item">
                  <Link to="/projects">Projects</Link>
                </li>
                <li className="nav-item">
                  <Link to="/contact" className="nav-link">
                    Contact
                  </Link>
                </li>
                {/* SignIn/Up */}
                {authState ? (
                  // Display user info and sign-out link
                  <li className="nav-item">
                    <span>{authState.nombre}</span>
                    <button onClick={signOut}>Sign Out</button>
                  </li>
                ) : (
                  // Display sign-in/sign-up links
                  <li className="nav-item">
                    <Link to="/auth">Sign In</Link>
                  </li>
                )}
              </ul>

              <div className="side-nav">
                <Link to="#">Book A Schedule</Link>
              </div>
            </div>
          </nav>
        </div>
      </div>
    </div>
  );
};
export default Navbar;
