import React, { useState } from 'react';

const AuthForm = () => {
  const [isSignIn, setIsSignIn] = useState(true);
  const [emailOrPhone, setEmailOrPhone] = useState('');
  const [password, setPassword] = useState('');
  // Additional state for sign-up mode
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = {
      emailOrPhone, 
      password,
      // Include additional fields when not in sign-in mode
      ...(isSignIn ? {} : { firstName, lastName, confirmPassword })
    };
    console.log(formData);
    // Here you would send formData to your backend
  };

  const toggleMode = () => {
    setIsSignIn(!isSignIn);
  };

  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <div className="user-item">
          <h2>{isSignIn ? 'Sign In' : 'Sign Up'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="row">
              {!isSignIn && (
                <>
                  <div className="col-lg-12">
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="First Name"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                      />
                    </div>
                  </div>
                  <div className="col-lg-12">
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Last Name"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                      />
                    </div>
                  </div>
                </>
              )}
              <div className="col-lg-12">
                <div className="form-group">
                  <input
                    type={isSignIn ? "text" : "email"}
                    className="form-control"
                    placeholder={isSignIn ? "Email or Phone" : "Email"}
                    value={emailOrPhone}
                    onChange={(e) => setEmailOrPhone(e.target.value)}
                  />
                </div>
              </div>
              <div className="col-lg-12">
                <div className="form-group">
                  <input
                    type="password"
                    className="form-control"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>
              </div>
              {!isSignIn && (
                <div className="col-lg-12">
                  <div className="form-group">
                    <input
                      type="password"
                      className="form-control"
                      placeholder="Confirm Password"
                      value={confirmPassword}
                      onChange={(e) => setConfirmPassword(e.target.value)}
                    />
                  </div>
                </div>
              )}
              <div className="col-lg-12">
                <button type="submit" className="btn cmn-btn">
                  {isSignIn ? 'Sign In' : 'Sign Up'}
                </button>
                <span> Or </span>
                <button type="button" className="btn-toggle" onClick={toggleMode}>
                  {isSignIn ? 'Sign Up' : 'Sign In'}
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
