import React, { useState } from "react";

const AuthForm = ({ userType }) => {
  const [isSignIn, setIsSignIn] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState(""); // Assuming this is both first and last name

  // If you have more specific fields for 'Professional', such as 'especialidad',
  // you will need to add state and form inputs for them as well.

  const handleSubmit = (event) => {
    event.preventDefault();
    if (isSignIn) {
      // Sign-in logic here
      console.log("Sign in", { email, password });
    } else {
      // Sign-up logic here
      console.log("Sign up", { name, email, password, userType });
    }
    // You will need to send this data to your server/API
  };

  const toggleMode = () => {
    setIsSignIn(!isSignIn);
  };

  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
        <form onSubmit={handleSubmit}>
          {!isSignIn && (
            <div className="form-group">
              <input
                type="text"
                className="form-control"
                placeholder="Full Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
          )}
          <div className="form-group">
            <input
              type="email"
              className="form-control"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit" className="btn cmn-btn">
            {isSignIn ? "Sign In" : "Sign Up"}
          </button>
          <button type="button" className="btn cmn-btn" onClick={toggleMode}>
            {isSignIn
              ? "Need an account? Sign Up"
              : "Already have an account? Sign In"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AuthForm;
