import React, { useState, useContext } from "react";

// Define a context for the user type (if you are using context in your application)
const UserContext = React.createContext(null);

const AuthForm = () => {
  // If you're using context to manage user type, you can get it like this
  // const { userType } = useContext(UserContext);
  // For now, I will hardcode it, but you should use the above line if you have a context set up
  const userType = "Client"; // or 'Professional', depending on where you get this from

  const [isSignIn, setIsSignIn] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  // Additional state variables for sign up
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    if (isSignIn) {
      // Sign-in logic here
      console.log("Sign in", { email, password });
    } else {
      // Sign-up logic here
      console.log("Sign up", {
        firstName,
        lastName,
        email,
        phone,
        password,
        confirmPassword,
      });
    }
    // You should send this data to your server/API
  };

  const toggleMode = () => {
    setIsSignIn(!isSignIn);
  };

  return (
    <div className="auth-form-area">
      <div className="container">
        <h2>
          {isSignIn ? "Sign In" : "Sign Up"} as {userType}
        </h2>
        <form onSubmit={handleSubmit}>
          {!isSignIn && (
            <>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="First Name"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                />
              </div>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Last Name"
                  value={lastName}
                  onChange={(e) => setLastName(e.target.value)}
                />
              </div>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Phone"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                />
              </div>
            </>
          )}
          <div className="form-group">
            <input
              type="email"
              className="form-control"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          {!isSignIn && (
            <div className="form-group">
              <input
                type="password"
                className="form-control"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </div>
          )}
          <div className="form-group action">
            <button type="submit" className="btn btn-primary">
              {isSignIn ? "Sign In" : "Sign Up"}
            </button>
            <button type="button" className="btn btn-link" onClick={toggleMode}>
              {isSignIn
                ? "Need an account? Sign Up"
                : "Already have an account? Sign In"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AuthForm;
