import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./AuthContext";

const AuthForm = () => {
  const navigate = useNavigate();
  const { signIn } = useAuth();
  const [isSignIn, setIsSignIn] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombre, setNombre] = useState("");
  const [especialidad, setEspecialidad] = useState("");
  const [tipo, setTipo] = useState("user");
  const [errorMessages, setErrorMessages] = useState({});

  // Clears the error messages when the form type is toggled
  const clearErrors = () => setErrorMessages({});

  const validateForm = () => {
    let isValid = true;
    let errors = {};

    // Check if the email is empty
    if (!email.trim()) {
      isValid = false;
      errors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(email.trim())) {
      // Check if the email format is incorrect (after it's not empty)
      isValid = false;
      errors.email = "Email is invalid";
    }

    // Check if the password is empty
    if (!password.trim()) {
      isValid = false;
      errors.password = "Password is required";
    }

    if (!isSignIn) {
      if (!nombre.trim()) {
        isValid = false;
        errors.nombre = "Name is required";
      }
      if (tipo === "professional" && !especialidad.trim()) {
        isValid = false;
        errors.especialidad = "Specialty is required for professionals";
      }
    }

    setErrorMessages(errors);
    return isValid;
  };

  const postCredentials = async (endpoint, credentials) => {
    try {
      const response = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials),
      });

      if (!response.ok)
        throw new Error(`HTTP error! status: ${response.status}`);
      return response.json();
    } catch (error) {
      console.error("There was an error!", error);
    }
  };

  const handleLogin = async () => {
    const endpoint = "http://localhost/Tarea3/backend/src/index.php/login";
    const data = await postCredentials(endpoint, { email, password });

    if (data && !data.error) {
      signIn({ ...data, userName: data.nombre });
      navigate("/");
    } else {
      setErrorMessages({ form: "Login failed: " + data.message });
    }
  };

  const handleRegister = async () => {
    const endpoint =
      tipo === "professional"
        ? "http://localhost/Tarea3/backend/src/index.php/registerProfessional"
        : "http://localhost/Tarea3/backend/src/index.php/register";

    const payload = {
      nombre,
      email,
      password,
      tipo,
      ...(tipo === "professional" && { especialidad }),
    };

    const data = await postCredentials(endpoint, payload);

    if (data && !data.error) {
      navigate("/");
    } else {
      setErrorMessages({ form: "Registration failed: " + data.message });
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    clearErrors();

    if (!validateForm()) {
      return; // Stop the form submission if validation fails
    }

    if (isSignIn) {
      await handleLogin();
    } else {
      await handleRegister();
    }
  };

  const toggleTipo = () => {
    // This should toggle between 'user' and 'professional'
    setTipo((prevTipo) => (prevTipo === "user" ? "professional" : "user"));
    // If toggling to 'user', reset 'especialidad'
    if (tipo === "professional") {
      setEspecialidad("");
    }
  };

  const toggleMode = () => {
    // This should toggle between sign in and sign up
    setIsSignIn((prevIsSignIn) => !prevIsSignIn);
    // Reset fields when toggling mode
    setEmail("");
    setPassword("");
    setNombre("");
    setEspecialidad("");
    setTipo("user");
    setErrorMessages({});
  };

  return (
    <div className="user-form-area ptb-100">
      <div className="container">
        <div className="user-item">
          <h2>{isSignIn ? "Sign In" : "Sign Up"}</h2>
          <form onSubmit={handleSubmit}>
            {!isSignIn && (
              <>
                <div className="form-group">
                  <input
                    type="text"
                    className={`form-control ${
                      errorMessages.nombre ? "is-invalid" : ""
                    }`}
                    placeholder="Nombre"
                    value={nombre}
                    onChange={(e) => setNombre(e.target.value)}
                  />
                  {errorMessages.nombre && (
                    <div className="error-message">{errorMessages.nombre}</div>
                  )}
                </div>
                {tipo === "professional" && (
                  <div className="form-group">
                    <input
                      type="text"
                      className={`form-control ${
                        errorMessages.especialidad ? "is-invalid" : ""
                      }`}
                      placeholder="Especialidad"
                      value={especialidad}
                      onChange={(e) => setEspecialidad(e.target.value)}
                    />
                    {errorMessages.especialidad && (
                      <div className="error-message">
                        {errorMessages.especialidad}
                      </div>
                    )}
                  </div>
                )}
              </>
            )}
            <div className="form-group">
              <input
                type="email"
                className={`form-control ${
                  errorMessages.email ? "is-invalid" : ""
                }`}
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              {errorMessages.email && (
                <div className="error-message">{errorMessages.email}</div>
              )}
            </div>
            <div className="form-group">
              <input
                type="password"
                className={`form-control ${
                  errorMessages.password ? "is-invalid" : ""
                }`}
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              {errorMessages.password && (
                <div className="error-message">{errorMessages.password}</div>
              )}
            </div>
            {!isSignIn && (
              <div className="form-group">
                <label>
                  <input
                    type="checkbox"
                    checked={tipo === "professional"}
                    onChange={toggleTipo}
                  />
                  Register as a professional
                </label>
              </div>
            )}
            <div className="form-group">
              <button
                type="submit"
                className="btn cmn-btn"
                style={{ marginRight: "10px" }}
              >
                {isSignIn ? "Sign In" : "Sign Up"}
              </button>
              <button
                type="button"
                className="btn cmn-btn"
                onClick={toggleMode}
              >
                {isSignIn
                  ? "Need an account? Sign Up"
                  : "Have an account? Sign In"}
              </button>
            </div>
            {errorMessages.form && (
              <div className="error-message text-center">
                {errorMessages.form}
              </div>
            )}
          </form>
        </div>
      </div>
      <style jsx>{`
        .is-invalid {
          border: 2px solid #dc3545;
        }

        .error-message {
          color: #dc3545;
          font-size: 0.875em;
          margin-top: 0.25rem;
        }
      `}</style>
    </div>
  );
};
export default AuthForm;
